// Validate grid layout items to prevent minW > maxW errors
export const validateLayoutItem = (item) => {
  const validated = {
    i: item.i,
    x: Math.max(0, Number(item.x) || 0),
    y: Math.max(0, Number(item.y) || 0),
    w: Math.max(1, Number(item.w) || 4),
    h: Math.max(1, Number(item.h) || 4)
  };

  // Ensure minW ≤ w ≤ maxW
  validated.minW = Math.min(validated.w, Number(item.minW) || 1);
  validated.maxW = Math.max(validated.w, Number(item.maxW) || 12);
  
  // Ensure minH ≤ h ≤ maxH  
  validated.minH = Math.min(validated.h, Number(item.minH) || 1);
  validated.maxH = Math.max(validated.h, Number(item.maxH) || 12);

  return validated;
};

export const validateLayout = (layout) => {
  if (!Array.isArray(layout)) return [];
  return layout.map(validateLayoutItem);
};
