// src/components/Dashboard/LayoutGrid.jsx
import { usePersonalizationContext } from '../../context/PersonalizationContext';

export default function Dashboard() {
  const { userProfile, getLayout } = usePersonalizationContext();
  const [layout, setLayout] = useState(getLayout(userProfile.type));

  // Update layout when profile changes
  useEffect(() => {
    setLayout(getLayout(userProfile.type));
  }, [userProfile.type]);

  return (
    <ReactGridLayout>
      {layout.widgets.map(widget => (
        <div key={widget.i} data-grid={widget}>
          <WidgetFactory type={widget.type} config={widget.config} />
        </div>
      ))}
    </ReactGridLayout>
  );
}
