import React from 'react';
import { OrderBook } from '@lab49/react-order-book';

// Validation function based on search results
const isValidNumber = (input) => {
  if (input === null || input === undefined || input === '') return false;
  const num = parseFloat(input);
  return !isNaN(num) && isFinite(num);
};

// Safe number formatting for big.js
const sanitizeForBig = (value) => {
  if (!isValidNumber(value)) return '0';
  
  // Convert to string and remove any non-numeric characters except decimal point
  const cleaned = String(value).replace(/[^0-9.-]/g, '');
  
  // Ensure it's a valid number format
  const parsed = parseFloat(cleaned);
  return isValidNumber(parsed) ? parsed.toString() : '0';
};

export default function OrderBookWidget() {
  // Sample data with proper validation for your fintech dashboard
  const book = {
    asks: [
      { 
        price: sanitizeForBig('3450.50'), 
        size: sanitizeForBig('1200') 
      },
      { 
        price: sanitizeForBig('3451.25'), 
        size: sanitizeForBig('800') 
      },
      { 
        price: sanitizeForBig('3452.00'), 
        size: sanitizeForBig('1500') 
      }
    ],
    bids: [
      { 
        price: sanitizeForBig('3449.75'), 
        size: sanitizeForBig('2100') 
      },
      { 
        price: sanitizeForBig('3449.25'), 
        size: sanitizeForBig('1800') 
      },
      { 
        price: sanitizeForBig('3448.50'), 
        size: sanitizeForBig('950') 
      }
    ]
  };

  return (
    <div className="widget order-book-widget">
      <div className="widget-header">
        <h3>NIFTY 50 Order Book</h3>
        <span className="live-indicator">ðŸŸ¢ Live</span>
      </div>
      <OrderBook 
        book={book}
        askColor="#F59E0B"
        bidColor="#00F0A0"
        showSpread={true}
        listLength={10}
      />
    </div>
  );
}
