import React, { useEffect, useRef } from 'react';

export default function TradingViewWidget({ symbol = "NIFTY1!" }) {
  const containerRef = useRef(null);
  const widgetRef = useRef(null);

  useEffect(() => {
    // Clear any existing widget
    if (containerRef.current) {
      containerRef.current.innerHTML = '';
    }

    // Load TradingView script
    const loadTradingViewScript = () => {
      return new Promise((resolve) => {
        if (window.TradingView) {
          resolve();
          return;
        }

        const script = document.createElement('script');
        script.src = 'https://s3.tradingview.com/tv.js';
        script.async = true;
        script.onload = resolve;
        document.head.appendChild(script);
      });
    };

    loadTradingViewScript().then(() => {
      if (containerRef.current && window.TradingView) {
        // Generate unique container ID
        const containerId = `tradingview_${Math.random().toString(36).substr(2, 9)}`;
        containerRef.current.id = containerId;

        widgetRef.current = new window.TradingView.widget({
          width: "100%",
          height: "100%",
          symbol: symbol, // Use correct NIFTY symbol format
          interval: "D",
          timezone: "Asia/Kolkata",
          theme: "dark",
          style: "1",
          locale: "en",
          toolbar_bg: "#1E293B",
          enable_publishing: false,
          allow_symbol_change: true,
          container_id: containerId,
          hide_side_toolbar: false,
          studies: ["MACD@tv-basicstudies"],
          withdateranges: true,
          hide_image_invalid_symbol: false, // Show invalid symbol icon if needed
          autosize: true
        });
      }
    });

    return () => {
      if (widgetRef.current && widgetRef.current.remove) {
        widgetRef.current.remove();
      }
    };
  }, [symbol]);

  return (
    <div className="widget trading-chart">
      <div className="widget-header">
        <h3>NIFTY 50 Chart</h3>
        <span className="live-indicator">
          <span className="status-dot"></span>
          Live
        </span>
      </div>
      <div 
        ref={containerRef} 
        style={{ 
          width: '100%', 
          height: 'calc(100% - 60px)', 
          minHeight: '400px' 
        }}
      />
    </div>
  );
}
