import React from 'react';
import { OrderBook } from '@lab49/react-order-book';

const sanitizeNumber = (num) => 
  typeof num === 'string' 
    ? num.replace(/[^0-9.]/g, '') 
    : num.toFixed(2).replace(/[^0-9.]/g, '');

export default function OrderBookWidget() {
  const book = {
    asks: [
      { price: sanitizeNumber(3450.50), size: sanitizeNumber(1200) },
      { price: sanitizeNumber(3451.00), size: sanitizeNumber(2300) }
    ],
    bids: [
      { price: sanitizeNumber(3449.75), size: sanitizeNumber(3400) },
      { price: sanitizeNumber(3449.50), size: sanitizeNumber(2100) }
    ]
  };

  return (
    <div className="order-book-widget">
      <h3>NIFTY 50 Order Book</h3>
      <OrderBook 
        book={book}
        askColor="#F59E0B"
        bidColor="#00F0A0"
        showSpread
      />
    </div>
  );
}
