// src/components/Dashboard/LayoutGrid.jsx
import React from 'react';
import { Responsive, WidthProvider } from 'react-grid-layout';
import { usePersonalizationContext } from '../../context/PersonalizationContext'; // Correct import
import WidgetFactory from './WidgetFactory';
import 'react-grid-layout/css/styles.css';

const ResponsiveGridLayout = WidthProvider(Responsive);

export default function LayoutGrid() {
  const { getLayout } = usePersonalizationContext();
  const { layout = [], widgets = [] } = getLayout();

  return (
    <ResponsiveGridLayout
      layouts={{ lg: layout }}
      // ... rest of props
    >
      {widgets.map(widget => (
        <div key={widget.i} data-grid={layout.find(l => l.i === widget.i)}>
          <WidgetFactory type={widget.type} />
        </div>
      ))}
    </ResponsiveGridLayout>
  );
}
