import React, { useState } from 'react';
import { usePersonalizationContext } from '../../context/PersonalizationContext';

export default function VoiceAssistant() {
  const [isListening, setIsListening] = useState(false);
  const [transcript, setTranscript] = useState('');
  const [response, setResponse] = useState('');
  const { updateProfile } = usePersonalizationContext();

  // Command processing function
  const processCommand = (command) => {
    const lowerCommand = command.toLowerCase();
    
    if (lowerCommand.includes('show me nifty') || lowerCommand.includes('nifty analysis')) {
      setResponse('Showing NIFTY 50 analysis with technical indicators...');
      // Could trigger chart symbol change here
    } else if (lowerCommand.includes('day trader') || lowerCommand.includes('switch to day trader')) {
      updateProfile({ type: 'dayTrader' });
      setResponse('Switched to Day Trader layout with order book and real-time data.');
    } else if (lowerCommand.includes('investor') || lowerCommand.includes('long term')) {
      updateProfile({ type: 'investor' });
      setResponse('Switched to Long-Term Investor layout with fundamentals.');
    } else if (lowerCommand.includes('undervalued stocks')) {
      setResponse('Scanning for undervalued stocks with high institutional buying: TITAN, HDFC Bank, TCS show strong fundamentals...');
    } else if (lowerCommand.includes('market sentiment')) {
      setResponse('Current market sentiment: Bullish (67%). Banking sector leading gains, tech sector mixed.');
    } else {
      setResponse('I can help with: "Show NIFTY analysis", "Switch to day trader", "Find undervalued stocks", or "Market sentiment"');
    }
  };

  const handleVoiceClick = () => {
    if (!isListening) {
      setIsListening(true);
      setTranscript('Listening...');
      setResponse('');
      
      // Use Web Speech API
      if ('webkitSpeechRecognition' in window || 'SpeechRecognition' in window) {
        const SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition;
        const recognition = new SpeechRecognition();
        
        recognition.continuous = false;
        recognition.interimResults = false;
        recognition.lang = 'en-US';
        
        recognition.onresult = (event) => {
          const spokenText = event.results[0][0].transcript;
          setTranscript(spokenText);
          processCommand(spokenText);
          setIsListening(false);
        };
        
        recognition.onerror = () => {
          setTranscript('Sorry, I couldn\'t understand that.');
          setIsListening(false);
        };
        
        recognition.onend = () => {
          setIsListening(false);
        };
        
        recognition.start();
      } else {
        // Fallback for browsers without speech recognition
        setTimeout(() => {
          const simulatedCommands = [
            'Show me NIFTY 50 analysis',
            'What are some undervalued stocks?',
            'Switch to day trader layout'
          ];
          const randomCommand = simulatedCommands[Math.floor(Math.random() * simulatedCommands.length)];
          setTranscript(randomCommand);
          processCommand(randomCommand);
          setIsListening(false);
        }, 2000);
      }
    }
  };

  return (
    <div className="widget voice-assistant">
      <div className="widget-header">
        <h3>AI Assistant</h3>
      </div>
      <button 
        className={`voice-button ${isListening ? 'listening' : ''}`}
        onClick={handleVoiceClick}
        disabled={isListening}
      >
        ðŸŽ¤ {isListening ? 'Listening...' : 'Click to Speak'}
      </button>
      
      {transcript && (
        <div className="transcript">
          <strong>You said:</strong> "{transcript}"
        </div>
      )}
      
      {response && (
        <div className="ai-response">
          <strong>AI:</strong> {response}
        </div>
      )}
      
      <div className="suggested-commands">
        <p className="commands-label">Try saying:</p>
        <ul>
          <li>"Show me NIFTY 50 analysis"</li>
          <li>"Find undervalued stocks"</li>
          <li>"Switch to day trader layout"</li>
          <li>"What's the market sentiment?"</li>
        </ul>
      </div>
    </div>
  );
}
