import React from 'react';
import { Responsive, WidthProvider } from 'react-grid-layout';
import { usePersonalizationContext } from '../../context/PersonalizationContext';
import { validateLayout } from '../../utils/layout-validation';
import WidgetFactory from './WidgetFactory';
import ErrorBoundary from '../ErrorBoundary';
import 'react-grid-layout/css/styles.css';
import 'react-resizable/css/styles.css';

const ResponsiveGridLayout = WidthProvider(Responsive);

export default function LayoutGrid() {
  const { getLayout } = usePersonalizationContext();
  const layoutData = getLayout();
  
  // Validate layout to prevent minW > maxW errors
  const validatedLayout = validateLayout(layoutData?.layout || []);
  const widgets = layoutData?.widgets || [];

  if (validatedLayout.length === 0 || widgets.length === 0) {
    return (
      <div className="financial-error">
        <div className="error-icon">⚠️</div>
        <h3>Market Data Temporarily Unavailable</h3>
        <p>We're experiencing issues loading real-time market data.</p>
        <button 
          onClick={() => window.location.reload()}
          className="retry-button"
        >
          Retry Loading Data
        </button>
      </div>
    );
  }

  return (
    <ResponsiveGridLayout
      className="dashboard-grid"
      layouts={{ lg: validatedLayout }}
      breakpoints={{ lg: 1200, md: 996, sm: 768, xs: 480, xxs: 0 }}
      cols={{ lg: 12, md: 10, sm: 6, xs: 4, xxs: 2 }}
      rowHeight={40}
      margin={[16, 16]}
      containerPadding={[0, 0]}
      useCSSTransforms={true}
      preventCollision={false}
      isDraggable={true}
      isResizable={true}
      draggableHandle=".drag-handle"
    >
      {widgets.map((widget) => {
        const layoutItem = validatedLayout.find(l => l.i === widget.i);
        return (
          <div key={widget.i} data-grid={layoutItem}>
            <ErrorBoundary>
              <div className="drag-handle">⋮⋮</div>
              <WidgetFactory type={widget.type} />
            </ErrorBoundary>
          </div>
        );
      })}
    </ResponsiveGridLayout>
  );
}
