export default function ChallengesWidget() {
  const challenges = [
    { title: 'Weekly Profit Challenge', target: '5% Return', progress: '3/5 Days' },
    { title: 'Risk Management Master', target: 'Max 2% Drawdown', progress: '85%' },
  ];

  return (
    <div className="widget challenges">
      <h3>Active Challenges</h3>
      {challenges.map((challenge) => (
        <div key={challenge.title} className="challenge-item">
          <div className="challenge-header">
            <h4>{challenge.title}</h4>
            <span className="status">Ongoing</span>
          </div>
          <div className="progress-bar">
            <div 
              className="progress-fill" 
              style={{ width: challenge.progress }}
            />
          </div>
          <div className="challenge-meta">
            <span>Target: {challenge.target}</span>
            <span>Progress: {challenge.progress}</span>
          </div>
        </div>
      ))}
    </div>
  );
}
